//http request
function httpGetAsync(theUrl, callback) {
    var xmlHttp = new XMLHttpRequest();
    xmlHttp.onreadystatechange = function() { 
      if (xmlHttp.readyState === 4 && xmlHttp.status === 200){
        callback(xmlHttp.responseText, theUrl);
      }
    }
    xmlHttp.open("GET", theUrl, true); // true for asynchronous 
    xmlHttp.send(null);
}
function getWeather(text, theUrl) {
  let value = text.match(/[0-9]{2}\.[0-9]/)[0];
  document.getElementById('temp').innerHTML = 'Current temperature: ' + value + ' F';
  document.getElementById('temp').onclick = function () {
    chrome.tabs.create({active: true, 'url': theUrl});
  };
}
