function summary(text) {
  let sum = text.match(/<\/tbody><\/table><\/div><div class="esc-lead-snippet-wrapper.*esc-extension-wrapper/g)[0];

  let miniSummary = sum.replace(/<\/tbody><\/table><\/div><div class="esc-lead-snippet-wrapper/, '').replace(/esc-extension-wrapper/, '')
  .replace(/<b>/g, '').replace(/<\/b>/g, '').replace(/<div>/g, '').replace(/<\/div>/g, '').replace(/[<>]/g, '').replace(/div class=/, '');

  document.getElementById('summary').innerHTML = miniSummary;
  // alert(miniSummary);
}